local input =
	""

local getStringNum = string.gmatch(input, ".")
local function getNum()
	return function()
		local n = getStringNum()
		if n ~= nil then
			return tonumber(n)
		end
	end
end

local ret = 1

while true do
	local nums = {}
	while true do
		local n = getNum()()
		if n == 0 then
			break
		end

		if n == nil then
			print(ret)
			return
		end

		table.insert(nums, n)
	end

	if #nums >= 13 then
		local curr = 1
		for i = 1, 13, 1 do
			curr = curr * nums[i]
		end
		ret = math.max(curr, ret)

		for i = 14, #nums, 1 do
			local num = nums[i]
			curr = (curr / nums[i - 13]) * nums[i]
			ret = math.max(curr, ret)
		end
	end
end
